import { emitter } from './events.js';
export const notify = (args) => {
    if (typeof args === 'string') {
        args = { title: '', text: args };
    }
    if (typeof args === 'object') {
        emitter.emit('add', args);
    }
};
notify.close = function (id) {
    emitter.emit('close', id);
};
